import React, { useRef } from 'react';
import { useFormContext } from './FormContext'; // Adjust path if needed
import ReactToPrint from 'react-to-print'; // ✅ This is correct for default export


const ApplicationPdfReport = () => {
  const { formData } = useFormContext();  // ✅ use context instead of location.state
  const componentRef = useRef();

  const renderField = (label, value) => (
    <div style={{ marginBottom: '8px' }}>
      <strong>{label}:</strong> {value || 'N/A'}
    </div>
  );

  if (!formData || Object.keys(formData).length === 0) {
    return <div style={{ padding: '2rem', color: 'red' }}>No form data found. Please complete the form.</div>;
  }

  return (
    <div style={{ padding: '2rem' }}>
      <ReactToPrint
        trigger={() => <button style={{ float: 'right' }}>Print / Save PDF</button>}
        content={() => componentRef.current}
      />
      <div ref={componentRef}>
        <h1>Application Summary - BPT 2025</h1>

        <h2>Advertisement Details</h2>
        {renderField('Ad No.', formData.adNo)}
        {renderField('Ad Date', formData.adDate)}
        {renderField('Application No.', formData.applicationNo)}
        {renderField('Course Code', formData.courseCode)}
        {renderField('Course Name', formData.courseName)}
        {renderField('Date of Entry', formData.dateOfEntry)}
        {renderField('Last Date', formData.lastDate)}
        {renderField('Department', formData.department)}

        <h2>Applicant Details</h2>
        {renderField('Name', formData.name)}
        {renderField('Father\'s Name', formData.fatherName)}
        {renderField('DOB', formData.dob)}
        {renderField('Age', formData.age)}
        {renderField('Place of Birth', formData.placeOfBirth)}
        {renderField('Social Status', formData.socialStatus)}
        {renderField('Nationality', formData.nationality)}
        {renderField('Gender', formData.gender)}
        {renderField('Marital Status', formData.maritalStatus)}
        {renderField('Differently Abled', formData.differentlyAbled)}
        {renderField('ID Mark 1', formData.idMark1)}
        {renderField('ID Mark 2', formData.idMark2)}
        {renderField('Aadhar', formData.aadhar)}
        {renderField('Father Email', formData.fatherEmail)}

        <h2>Educational Qualifications</h2>
        {renderField('10th School Name', formData.tenthSchool)}
        {renderField('Intermediate 1st Year', formData.inter1stCollege)}
        {renderField('Intermediate 2nd Year', formData.inter2ndCollege)}
        {renderField('EAPCET Reg. No.', formData.registrationNumber)}
        {renderField('EAPCET Hall Ticket', formData.hallTicketNumber)}
        {renderField('EAPCET Rank', formData.rank)}

        <h2>Address Details</h2>
        {renderField('Correspondence Address', formData.correspondenceAddress)}
        {renderField('Permanent Address', formData.permanentAddress)}

        <h2>Payment Details</h2>
        {renderField('Payment Type', formData.paymentType)}
        {renderField('Transaction ID', formData.transactionId)}
        {renderField('Payment Date', formData.date)}
        {renderField('Bank Name', formData.bankName)}
        {renderField('Branch', formData.branchDetails)}
        {renderField('Amount', formData.amount)}
        {renderField('Remarks', formData.remarks)}
      </div>
    </div>
  );
};

export default ApplicationPdfReport;
