      applicant_code: '',
      course_code: '',
      year: '',
      semester: '',
      exam_code: '',
      subject_code: '',
      paper_code: '',
      marks_obtained: ''
    }
  ]);

  // --- Handlers for E.1: Course Subject Master ---
  const handleSubjectChange = (index, field, value) => {
    const updated = [...courseSubjects];
    updated[index][field] = value;
    setCourseSubjects(updated);
  };

  const addSubjectRow = () => {
    setCourseSubjects([
      ...courseSubjects,
      {
        course_name: '',
        course_code: '',
        year: '',
        semester: '',
        subject_code: '',
        subject_name: '',
        paper_code: '',
        exam_code: '',
        max_marks: ''
      }
    ]);
  };

  // --- Handlers for E.2: Student Marks Obtained ---
  const handleRecordChange = (index, field, value) => {
    const updated = [...studentRecords];
    updated[index][field] = value;
    setStudentRecords(updated);
  };

  const addRecordRow = () => {
    setStudentRecords([
      ...studentRecords,
      {
        applicant_code: '',
        course_code: '',
        year: '',
        semester: '',
        exam_code: '',
        subject_code: '',
        paper_code: '',
        marks_obtained: ''
      }
    ]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('E.1 Course Subjects:', courseSubjects);
    console.log('E.2 Student Records:', studentRecords);
    alert('Data captured in console!');
  };

  return (
    <form onSubmit={handleSubmit} className="p-4 max-w-5xl mx-auto space-y-8 bg-white shadow rounded">
      <h2 className="text-xl font-bold">E.1 - Course Subject Master Details</h2>
      {courseSubjects.map((subject, index) => (
        <div key={index} className="grid grid-cols-2 gap-4 border p-4 rounded">
          <input type="text" placeholder="Course Name" value={subject.course_name} onChange={(e) => handleSubjectChange(index, 'course_name', e.target.value)} />
          <input type="text" placeholder="Course Code" value={subject.course_code} onChange={(e) => handleSubjectChange(index, 'course_code', e.target.value)} />
          <input type="number" placeholder="Year" value={subject.year} onChange={(e) => handleSubjectChange(index, 'year', e.target.value)} />
          <input type="text" placeholder="Semester (I/II)" value={subject.semester} onChange={(e) => handleSubjectChange(index, 'semester', e.target.value)} />
          <input type="text" placeholder="Subject Code" value={subject.subject_code} onChange={(e) => handleSubjectChange(index, 'subject_code', e.target.value)} />
          <input type="text" placeholder="Subject Name" value={subject.subject_name} onChange={(e) => handleSubjectChange(index, 'subject_name', e.target.value)} />
          <input type="text" placeholder="Paper Code (Theory/Practical)" value={subject.paper_code} onChange={(e) => handleSubjectChange(index, 'paper_code', e.target.value)} />
          <input type="text" placeholder="Exam Code (MidTerm/End)" value={subject.exam_code} onChange={(e) => handleSubjectChange(index, 'exam_code', e.target.value)} />
          <input type="number" placeholder="Max Marks" value={subject.max_marks} onChange={(e) => handleSubjectChange(index, 'max_marks', e.target.value)} />
        </div>
      ))}
      <button type="button" onClick={addSubjectRow} className="bg-blue-500 text-white px-4 py-2 rounded">+ Add Subject</button>

      <h2 className="text-xl font-bold pt-8">E.2 - Student Academic Records</h2>
      {studentRecords.map((record, index) => (
        <div key={index} className="grid grid-cols-2 gap-4 border p-4 rounded">
          <input type="text" placeholder="Applicant Code" value={record.applicant_code} onChange={(e) => handleRecordChange(index, 'applicant_code', e.target.value)} />
          <input type="text" placeholder="Course Code" value={record.course_code} onChange={(e) => handleRecordChange(index, 'course_code', e.target.value)} />
          <input type="number" placeholder="Year" value={record.year} onChange={(e) => handleRecordChange(index, 'year', e.target.value)} />
          <input type="text" placeholder="Semester" value={record.semester} onChange={(e) => handleRecordChange(index, 'semester', e.target.value)} />
          <input type="text" placeholder="Exam Code" value={record.exam_code} onChange={(e) => handleRecordChange(index, 'exam_code', e.target.value)} />
          <input type="text" placeholder="Subject Code" value={record.subject_code} onChange={(e) => handleRecordChange(index, 'subject_code', e.target.value)} />
          <input type="text" placeholder="Paper Code" value={record.paper_code} onChange={(e) => handleRecordChange(index, 'paper_code', e.target.value)} />
          <input type="number" placeholder="Marks Obtained" value={record.marks_obtained} onChange={(e) => handleRecordChange(index, 'marks_obtained', e.target.value)} />
        </div>
      ))}
      <button type="button" onClick={addRecordRow} className="bg-green-500 text-white px-4 py-2 rounded">+ Add Record</button>

      <div className="pt-6">
        <button type="submit" className="bg-indigo-600 text-white px-6 py-2 rounded">Submit</button>
      </div>
    </form>
  );
};

export default AcademicRecordForm;
