import React, { useState } from 'react';
import { Typography, TextField, Box } from '@mui/material';

const AdvertisementDetails = () => {
  const [adDetails, setAdDetails] = useState({
    adNo: '',
    adDate: '',
    applicationNo: '',
    courseCode: '',
    courseName: 'Bachelor of Physiotherapy',
    dateOfEntry: '2025-06-09T13:23:55',
    lastDate: '2025-06-28',
    department: 'Academic Section II'
  });

  const handleChange = (field, value) => {
    setAdDetails(prev => ({ ...prev, [field]: value }));
  };

  return (
    <Box sx={{ p: 3, border: '1px solid #ccc', borderRadius: 2, mb: 4 }}>
      <Typography variant="h6" gutterBottom>Advertisement Details</Typography>

      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2, mb: 2 }}>
        <TextField
          label="Ad. No."
          value={adDetails.adNo}
          onChange={(e) => handleChange('adNo', e.target.value)}
          size="small"
        />
        <TextField
          label="Ad. Date"
          type="date"
          value={adDetails.adDate}
          onChange={(e) => handleChange('adDate', e.target.value)}
          size="small"
          InputLabelProps={{ shrink: true }}
        />
        <TextField
          label="Application No."
          value={adDetails.applicationNo}
          onChange={(e) => handleChange('applicationNo', e.target.value)}
          size="small"
        />
        <TextField
          label="Course Code"
          value={adDetails.courseCode}
          onChange={(e) => handleChange('courseCode', e.target.value)}
          size="small"
        />
        <TextField
          label="Course Name"
          value={adDetails.courseName}
          onChange={(e) => handleChange('courseName', e.target.value)}
          size="small"
        />
        <TextField
          label="Date of Entry"
          type="datetime-local"
          value={adDetails.dateOfEntry}
          onChange={(e) => handleChange('dateOfEntry', e.target.value)}
          size="small"
          InputLabelProps={{ shrink: true }}
        />
        <TextField
          label="Last Date of Receiving Application"
          type="date"
          value={adDetails.lastDate}
          onChange={(e) => handleChange('lastDate', e.target.value)}
          size="small"
          InputLabelProps={{ shrink: true }}
        />
        <TextField
          label="Department"
          value={adDetails.department}
          onChange={(e) => handleChange('department', e.target.value)}
          size="small"
        />
      </Box>
    </Box>
  );
};

export default AdvertisementDetails;
