import React, { useEffect, useState } from 'react';
import { 
  Box, Typography, Table,   TableBody, TableCell,  TableContainer, TableHead,   TableRow,   Paper,TextField,
  Button 
} from '@mui/material';
import { toWords } from 'number-to-words';

const FormComponentBPT = ({ 
  title,
  subjectsData,
  name = 'John Doe',
  soName = 'Richard Doe',
  idLabel = 'Roll Number',
  rollNumber = 'BPT12345',
  examDate = 'June 2025',
  submissionDate = '2025-06-24',
  semester,
  course,
  department,
  year,
  sgpa,
  setSemester,
  setRollNumber,
  setCourse,
  setDepartment,
  setYear
}) => {
  const [subjects, setSubjects] = useState(subjectsData || []);
  const [grandTotal, setGrandTotal] = useState(0);
  const [totalInWords, setTotalInWords] = useState('');

  useEffect(() => {
    const total = subjects.reduce((acc, subj) => {
      const theory = Number(subj.theorySecured || 0);
      const practical = subj.practicalMax === 'NA' ? 0 : Number(subj.practicalSecured || 0);
      return acc + theory + practical;
    }, 0);
    setGrandTotal(total);
    setTotalInWords(toWords(total));
  }, [subjects]);

  return (
    <Box sx={{ pt: 9, px: 6, pb: 7 }}>
      <Typography variant="h6" gutterBottom>{title}</Typography>

      {/* Student Details Form */}
      <Box sx={{ mb: 3, display: 'flex', flexDirection: 'column', gap: 2 }}>
        <TextField 
          label={idLabel} 
          value={rollNumber} 
          onChange={(e) => setRollNumber(e.target.value)} 
        />
        <TextField 
          label="Course" 
          value={course} 
          onChange={(e) => setCourse(e.target.value)} 
          fullWidth 
        />
        <TextField 
          label="Department" 
          value={department} 
          onChange={(e) => setDepartment(e.target.value)} 
          fullWidth 
        />
        <TextField 
          label="Semester" 
          value={semester} 
          onChange={(e) => setSemester(e.target.value)} 
          fullWidth 
        />
        <TextField 
          label="Year" 
          value={year} 
          onChange={(e) => setYear(e.target.value)} 
          fullWidth 
        />
        <Button variant="outlined">Fetch</Button>
      </Box>

      {/* Student Information Display */}
      <Typography><strong>{idLabel}:</strong> {rollNumber}</Typography>
      <Typography><strong>Name:</strong> {name}</Typography>
      <Typography><strong>S/o. / D/o.:</strong> {soName}</Typography>
      <Typography><strong>Month & Year of Exam:</strong> {examDate}</Typography>

      {/* Marks Table */}
      <TableContainer component={Paper} sx={{ mt: 3 }}>
        <Table>
          <TableHead>
            <TableRow sx={{ backgroundColor: '#dddddd' }}>
              <TableCell sx={{ fontWeight: 'bold' }}>Subject</TableCell>
              <TableCell sx={{ fontWeight: 'bold' }} align="center" colSpan={3}>Theory</TableCell>
              <TableCell sx={{ fontWeight: 'bold' }} align="center" colSpan={3}>Practical / Viva</TableCell>
              <TableCell sx={{ fontWeight: 'bold' }} align="center" colSpan={3}>Total</TableCell>
              <TableCell sx={{ fontWeight: 'bold' }}>Result</TableCell>
            </TableRow>
            <TableRow sx={{ backgroundColor: '#dddddd' }}>
              <TableCell />
              <TableCell>Max</TableCell>
              <TableCell>Min</TableCell>
              <TableCell>Sec</TableCell>
              <TableCell>Max</TableCell>
              <TableCell>Min</TableCell>
              <TableCell>Sec</TableCell>
              <TableCell>Max</TableCell>
              <TableCell>Min</TableCell>
              <TableCell>Sec</TableCell>
              <TableCell />
            </TableRow>
          </TableHead>
          <TableBody>
            {subjects.map((subj, index) => {
              const totalSecured = subj.practicalMax === 'NA'
                ? Number(subj.theorySecured || 0)
                : Number(subj.theorySecured || 0) + Number(subj.practicalSecured || 0);

              return (
                <TableRow key={index}>
                  <TableCell sx={{ wordBreak: 'break-word' }}>{subj.name}</TableCell>
                  <TableCell>{subj.theoryMax}</TableCell>
                  <TableCell>{subj.theoryMin}</TableCell>
                  <TableCell>{subj.theorySecured || 0}</TableCell>
                  <TableCell>{subj.practicalMax}</TableCell>
                  <TableCell>{subj.practicalMin}</TableCell>
                  <TableCell>{subj.practicalMax === 'NA' ? 'NA' : subj.practicalSecured || 0}</TableCell>
                  <TableCell>{subj.totalMax}</TableCell>
                  <TableCell>{subj.totalMin}</TableCell>
                  <TableCell>{totalSecured}</TableCell>
                  <TableCell>{subj.result || '-'}</TableCell>
                </TableRow>
              );
            })}
            <TableRow>
              <TableCell colSpan={9} align="right"><strong>Grand Total:</strong></TableCell>
              <TableCell><strong>{grandTotal}</strong></TableCell>
              <TableCell><strong>T.M</strong></TableCell>
            </TableRow>
          </TableBody>
        </Table>
      </TableContainer>

      <Box sx={{ mt: 3 }}>
        <Typography><strong>Aggregate marks (in words):</strong> {totalInWords.toUpperCase()}</Typography>
      </Box>
      <Box sx={{ mt: 3, display: 'flex', justifyContent: 'space-between' }}>
        <Typography><strong>Dated:</strong> {submissionDate}</Typography>
      </Box>
    </Box>
  );
};

export default FormComponentBPT;
