import React, { useState } from 'react';
import {
  Box, Typography, TextField, Grid, Paper, Button, Divider,
  TableCell, TableContainer, TableHead, Table, TableRow, TableBody
} from '@mui/material';
import { useNavigate } from 'react-router-dom';

const ApplicationFormBPT = () => {
  const navigate = useNavigate();

  // Configuration objects for form sections
  const formConfig = {
    advertisementDetails: [
      { label: 'Ad. No.', field: 'adNo', xs: 6 },
      { label: 'Ad. Date', field: 'adDate', xs: 6 },
      { label: 'Application No.', field: 'applicationNo', xs: 6 },
      { label: 'Course Name', field: 'courseName', xs: 6 },
      { label: 'Course Code', field: 'courseCode', xs: 6 },
      { label: 'Date of Entry', field: 'dateOfEntry', xs: 6 },
      { label: 'Last Date of Application', field: 'lastDate', xs: 6 },
      { label: 'Department', field: 'department', xs: 6 }
    ],
    applicantDetails: [
      { label: 'Name', field: 'name', xs: 6 },
      { label: "Father's Name", field: 'fatherName', xs: 6 },
      { label: 'Date of Birth', field: 'dob', xs: 6 },
      { label: 'Age (on last date)', field: 'age', xs: 6 },
      { label: 'Place of Birth', field: 'birthPlace', xs: 6 },
      { label: 'Social Status', field: 'status', xs: 6 },
      { label: 'Nationality', field: 'nationality', xs: 6 },
      { label: 'Marital Status', field: 'maritalStatus', xs: 6 },
      { label: 'Gender', field: 'gender', xs: 6 },
      { label: 'Differently Abled', field: 'differentlyAbled', xs: 6 },
      { label: 'Identification Mark 1', field: 'idMark1', xs: 6 },
      { label: 'Identification Mark 2', field: 'idMark2', xs: 6 },
      { label: 'University Area', field: 'universityArea', xs: 6 },
      { label: 'In-service (Govt.)', field: 'inService', xs: 6 },
      { label: 'Aadhar', field: 'aadhar', xs: 6 },
      { label: "Father's Email", field: 'fatherEmail', xs: 6 }
    ],
    parentDetails: [
      { label: "Father's Name", field: 'fatherName', xs: 4 },
      { label: 'Age', field: 'fatherAge', xs: 4 },
      { label: 'Occupation', field: 'fatherOccupation', xs: 4 },
      { label: "Mother's Name", field: 'motherName', xs: 4 },
      { label: 'Age', field: 'motherAge', xs: 4 },
      { label: 'Occupation', field: 'motherOccupation', xs: 4 },
      { label: 'Monthly Income', field: 'monthlyIncome', xs: 4 }
    ],
    academicHistory: [
      { label: 'Inter 2nd Year', fields: [
        { label: 'Year', field: 'inter2ndYear', xs: 2 },
        { label: 'College', field: 'inter2ndCollege', xs: 4 },
        { label: 'Place', field: 'inter2ndPlace', xs: 2 },
        { label: 'State', field: 'inter2ndState', xs: 2 },
        { label: 'District', field: 'inter2ndDistrict', xs: 2 }
      ]},
      { label: 'Inter 1st Year', fields: [
        { label: 'Year', field: 'inter1stYear', xs: 2 },
        { label: 'College', field: 'inter1stCollege', xs: 4 },
        { label: 'Place', field: 'inter1stPlace', xs: 2 },
        { label: 'State', field: 'inter1stState', xs: 2 },
        { label: 'District', field: 'inter1stDistrict', xs: 2 }
      ]},
      { label: '10th Class', fields: [
        { label: 'Year', field: 'tenthYear', xs: 2 },
        { label: 'School', field: 'tenthSchool', xs: 4 },
        { label: 'Place', field: 'tenthPlace', xs: 2 },
        { label: 'State', field: 'tenthState', xs: 2 },
        { label: 'District', field: 'tenthDistrict', xs: 2 }
      ]},
      { label: '9th Class', fields: [
        { label: 'Year', field: 'ninthYear', xs: 2 },
        { label: 'School', field: 'ninthSchool', xs: 4 },
        { label: 'Place', field: 'ninthPlace', xs: 2 },
        { label: 'State', field: 'ninthState', xs: 2 },
        { label: 'District', field: 'ninthDistrict', xs: 2 }
      ]},
      { label: '8th Class', fields: [
        { label: 'Year', field: 'eighthYear', xs: 2 },
        { label: 'School', field: 'eighthSchool', xs: 4 },
        { label: 'Place', field: 'eighthPlace', xs: 2 },
        { label: 'State', field: 'eighthState', xs: 2 },
        { label: 'District', field: 'eighthDistrict', xs: 2 }
      ]},
      { label: '7th Class', fields: [
        { label: 'Year', field: 'seventhYear', xs: 2 },
        { label: 'School', field: 'seventhSchool', xs: 4 },
        { label: 'Place', field: 'seventhPlace', xs: 2 },
        { label: 'State', field: 'seventhState', xs: 2 },
        { label: 'District', field: 'seventhDistrict', xs: 2 }
      ]},
      { label: '6th Class', fields: [
        { label: 'Year', field: 'sixthYear', xs: 2 },
        { label: 'School', field: 'sixthSchool', xs: 4 },
        { label: 'Place', field: 'sixthPlace', xs: 2 },
        { label: 'State', field: 'sixthState', xs: 2 },
        { label: 'District', field: 'sixthDistrict', xs: 2 }
      ]}
    ],
    eapcetDetails: [
      { label: 'Registration Number', field: 'eapcetRegNo', xs: 4 },
      { label: 'Hall Ticket Number', field: 'eapcetHallTicket', xs: 4 },
      { label: 'Rank', field: 'eapcetRank', xs: 4 }
    ],
    addressDetails: [
      { label: 'Address', field: 'address', xs: 4 },
      { label: 'Country', field: 'country', xs: 4 },
      { label: 'State', field: 'state', xs: 4 },
      { label: 'District', field: 'district', xs: 4 },
      { label: 'Pincode', field: 'pincode', xs: 4 },
      { label: 'Mobile No', field: 'mobileno', xs: 4 },
      { label: 'Email', field: 'email', xs: 4 }
    ],
    subjects: [
      { key: 'botany', label: 'Botany' },
      { key: 'zoology', label: 'Zoology' },
      { key: 'physics', label: 'Physics' },
      { key: 'chemistry', label: 'Chemistry' },
      { key: 'english', label: 'English' },
      { key: 'secondLanguage', label: 'Second Language' }
    ]
  };

  const [formData, setFormData] = useState({
    // Personal Details
    adNo: '', adDate: '', applicationNo: '', courseName: '', courseCode: '', dateOfEntry: '', lastDate: '', 
    department: '', name: '', fatherName: '', dob: '', age: '', birthPlace: '', status: '', nationality: '', 
    maritalStatus: '', gender: '', differentlyAbled: '', idMark1: '', idMark2: '', universityArea: '',
    inService: '', aadhar: '', fatherEmail: '', fatherAge: '', fatherOccupation: '', motherName: '', 
    motherAge: '', motherOccupation: '', monthlyIncome: '', address: '', country: '', district: '', state: '',
    pincode: '', mobileno: '', email: '',
    // Intermediate Subjects
    botany: {
      theory1stYear: '', theory1stMax: '',
      theory2ndYear: '', theory2ndMax: '',
      practical: '', practicalMax: '',
      total: '', totalMax: '',
      maxmarks: ''
    },
    zoology: {
      theory1stYear: '', theory1stMax: '',
      theory2ndYear: '', theory2ndMax: '',
      practical: '', practicalMax: '',
      total: '', totalMax: '',
      maxmarks: ''
    },
    physics: {
      theory1stYear: '', theory1stMax: '',
      theory2ndYear: '', theory2ndMax: '',
      practical: '', practicalMax: '',
      total: '', totalMax: '',
      maxmarks: ''
    },
    chemistry: {
      theory1stYear: '', theory1stMax: '',
      theory2ndYear: '', theory2ndMax: '',
      practical: '', practicalMax: '',
      total: '', totalMax: ''
    },
    english: {
      theory1stYear: '', theory1stMax: '',
      theory2ndYear: '', theory2ndMax: '',
      practical: '', practicalMax: '',
      total: '', totalMax: ''
    },
    secondLanguage: {
      theory1stYear: '', theory1stMax: '',
      theory2ndYear: '', theory2ndMax: '',
      practical: '', practicalMax: '',
      total: '', totalMax: ''
    },
    // Totals
    overallMarks: '', overallMax: '', overallPercentage: '',
    groupMarks: '', groupMax: '', groupPercentage: '',
    // Academic History
    inter2ndYear: '', inter2ndCollege: '', inter2ndPlace: '',
    inter2ndState: '', inter2ndDistrict: '',
    inter1stYear: '', inter1stCollege: '', inter1stPlace: '',
    inter1stState: '', inter1stDistrict: '',
    tenthYear: '', tenthSchool: '', tenthPlace: '',
    tenthState: '', tenthDistrict: '',
    ninthYear: '', ninthSchool: '', ninthPlace: '',
    ninthState: '', ninthDistrict: '',
    eighthYear: '', eighthSchool: '', eighthPlace: '',
    eighthState: '', eighthDistrict: '',
    seventhYear: '', seventhSchool: '', seventhPlace: '',
    seventhState: '', seventhDistrict: '',
    sixthYear: '', sixthSchool: '', sixthPlace: '',
    sixthState: '', sixthDistrict: '',
    // EAPCET Details
    eapcetRegNo: '', eapcetHallTicket: '', eapcetRank: ''
  });

  const handleSubjectChange = (subject, field) => (e) => {
    setFormData(prev => ({
      ...prev,
      [subject]: {
        ...prev[subject],
        [field]: e.target.value
      }
    }));
  };

  const handleSimpleChange = (field) => (e) => {
    setFormData(prev => ({
      ...prev,
      [field]: e.target.value
    }));
  };

  const handleSubmit = () => {
    navigate('/report', { state: formData });
  };

  // Reusable form section component
  const FormSection = ({ title, config, isSubject = false }) => (
    <Paper elevation={2} sx={{ p: 2, mb: 3 }}>
      <Typography variant="subtitle1" fontWeight="bold">{title}</Typography>
      <Grid container spacing={2}>
        {config.map((item) => (
          <Grid item xs={item.xs} key={item.field || item.label}>
            <TextField
              label={item.label}
              fullWidth
              value={formData[item.field]}
              onChange={handleSimpleChange(item.field)}
            />
          </Grid>
        ))}
      </Grid>
    </Paper>
  );

  // Reusable academic history section component
  const AcademicHistorySection = ({ title, fields }) => (
    <>
      <Typography variant="body1" sx={{ mt: 3, fontWeight: 'bold' }}>{title}</Typography>
      <Grid container spacing={2}>
        {fields.map((field) => (
          <Grid item xs={field.xs} key={field.field}>
            <TextField
              label={field.label}
              fullWidth
              value={formData[field.field]}
              onChange={handleSimpleChange(field.field)}
            />
          </Grid>
        ))}
      </Grid>
    </>
  );

  // Reusable subject row component
  const SubjectRow = ({ subjectKey, subjectLabel }) => (
    <TableRow>
      <TableCell sx={{ fontWeight: 'bold' }}>{subjectLabel}</TableCell>
      <TableCell align="center">
        <TextField
          size="small"
          value={formData[subjectKey].theory1stYear}
          onChange={handleSubjectChange(subjectKey, 'theory1stYear')}
          InputProps={{ 
            endAdornment: ` / ${formData[subjectKey].theory1stMax}`,
            sx: { '& input': { textAlign: 'right' } }
          }}
          sx={{ width: 100 }}
        />
      </TableCell>
      <TableCell align="center">
        <TextField
          size="small"
          value={formData[subjectKey].theory2ndYear}
          onChange={handleSubjectChange(subjectKey, 'theory2ndYear')}
          InputProps={{ 
            endAdornment: ` / ${formData[subjectKey].theory2ndMax}`,
            sx: { '& input': { textAlign: 'right' } }
          }}
          sx={{ width: 100 }}
        />
      </TableCell>
      <TableCell align="center">
        <TextField
          size="small"
          value={formData[subjectKey].practical}
          onChange={handleSubjectChange(subjectKey, 'practical')}
          InputProps={{ 
            endAdornment: ` / ${formData[subjectKey].practicalMax}`,
            sx: { '& input': { textAlign: 'right' } }
          }}
          sx={{ width: 100 }}
        />
      </TableCell>
      <TableCell align="center">
        <TextField
          size="small"
          value={formData[subjectKey].total}
          onChange={handleSubjectChange(subjectKey, 'total')}
          InputProps={{ 
            endAdornment: ` / ${formData[subjectKey].totalMax}`,
            sx: { '& input': { textAlign: 'right' } }
          }}
          sx={{ width: 100 }}
        />
      </TableCell>
      <TableCell align="center">
        <TextField
          size="small"
          value={formData[subjectKey].total}
          onChange={handleSubjectChange(subjectKey, 'maximum marks')}
          InputProps={{ 
            endAdornment: ` / ${formData[subjectKey].maxmarks}`,
            sx: { '& input': { textAlign: 'right' } }
          }}
          sx={{ width: 100 }}
        />
      </TableCell>
    </TableRow>
  );

  return (
    <Box sx={{ pt: 4, px: 6, pb: 7 }}>
      <Typography variant="h5" gutterBottom align="center">
        APPLICATION FORM - Bachelor of Physiotherapy - 2025
      </Typography>

      <Grid container spacing={3}>
        {/* Advertisement Details */}
        <FormSection title="Advertisement Details" config={formConfig.advertisementDetails} />

        {/* Applicant's Details */}
        <FormSection title="Applicant's Details" config={formConfig.applicantDetails} />

        {/* Parent/Spouse Details */}
        <FormSection title="Parents' Details" config={formConfig.parentDetails} />

        {/* Intermediate Marks Section */}
        <Grid item xs={12}>
          <Paper elevation={2} sx={{ p: 2 }}>
            <Typography variant="subtitle1" fontWeight="bold">
              Intermediate Details (Telangana State)
            </Typography>
            <Typography variant="body2" color="text.secondary" gutterBottom>
              Intermediate Marks along with Practicals Details
            </Typography>

            <TableContainer>
              <Table size="small" sx={{ minWidth: 650 }}>
                <TableHead>
                  <TableRow sx={{ backgroundColor: '#f5f5f5' }}>
                    <TableCell sx={{ fontWeight: 'bold' }}>Subject</TableCell>
                    <TableCell sx={{ fontWeight: 'bold' }} align="center">1st Year Theory</TableCell>          
                    <TableCell sx={{ fontWeight: 'bold' }} align="center">2nd Year Theory</TableCell>
                    <TableCell sx={{ fontWeight: 'bold' }} align="center">Marks in Practicals</TableCell>
                    <TableCell sx={{ fontWeight: 'bold' }} align="center">Total Marks</TableCell>
                    <TableCell sx={{ fontWeight: 'bold' }} align="center">Maximum Marks</TableCell>
                  </TableRow>
                  <TableRow sx={{ backgroundColor: '#fafafa' }}>
                    <TableCell></TableCell>
                    <TableCell align="center">Obtained / Max</TableCell>
                    <TableCell align="center">Obtained / Max</TableCell>
                    <TableCell align="center">Obtained / Max</TableCell>
                    <TableCell align="center">Obtained / Max</TableCell>
                    <TableCell align="center">Obtained / Max</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {formConfig.subjects.map((subject) => (
                    <SubjectRow 
                      key={subject.key}
                      subjectKey={subject.key}
                      subjectLabel={subject.label}
                    />
                  ))}
                </TableBody>
              </Table>
            </TableContainer>

            <Typography variant="subtitle2" sx={{ mt: 3 }}>Totals</Typography>
            <Divider sx={{ my: 1 }} />
            <Grid container spacing={2}>
              <Grid item xs={4}>
                <TextField
                  label="Total Marks Obtained"
                  fullWidth
                  value={formData.overallMarks}
                  onChange={handleSimpleChange('overallMarks')}
                  InputProps={{ endAdornment: formData.overallMax && `/ ${formData.overallMax}` }}
                />
              </Grid>
              <Grid item xs={2}>
                <TextField
                  label="Percentage"
                  fullWidth
                  value={formData.overallPercentage}
                  onChange={handleSimpleChange('overallPercentage')}
                  InputProps={{ endAdornment: '%' }}
                />
              </Grid>
              <Grid item xs={4}>
                <TextField
                  label="Group Marks Obtained"
                  fullWidth
                  value={formData.groupMarks}
                  onChange={handleSimpleChange('groupMarks')}
                  InputProps={{ endAdornment: formData.groupMax && `/ ${formData.groupMax}` }}
                />
              </Grid>
              <Grid item xs={2}>
                <TextField
                  label="Group Percentage"
                  fullWidth
                  value={formData.groupPercentage}
                  onChange={handleSimpleChange('groupPercentage')}
                  InputProps={{ endAdornment: '%' }}
                />
              </Grid>
            </Grid>

            {/* Academic History */}
            <Typography variant="subtitle2" sx={{ mt: 3 }}>Academic History</Typography>
            <Divider sx={{ my: 1 }} />
            {formConfig.academicHistory.map((section) => (
              <AcademicHistorySection
                key={section.label}
                title={section.label}
                fields={section.fields}
              />
            ))}

            {/* EAPCET Details */}
            <Typography variant="subtitle2" sx={{ mt: 3 }}>TG EAPCET Details</Typography>
            <Divider sx={{ my: 1 }} />
            <Grid container spacing={2}>
              {formConfig.eapcetDetails.map((item) => (
                <Grid item xs={item.xs} key={item.field}>
                  <TextField
                    label={item.label}
                    fullWidth
                    value={formData[item.field]}
                    onChange={handleSimpleChange(item.field)}
                  />
                </Grid>
              ))}
            </Grid>

            {/* Address Correspondence */}
            <Typography variant="subtitle2" sx={{ mt: 3 }}>Address Correspondence</Typography>
            <Divider sx={{ my: 1 }} />
            <Grid container spacing={2}>
              {formConfig.addressDetails.map((item) => (
                <Grid item xs={item.xs} key={item.field}>
                  <TextField
                    label={item.label}
                    fullWidth
                    value={formData[item.field]}
                    onChange={handleSimpleChange(item.field)}
                  />
                </Grid>
              ))}
            </Grid>

            {/* Permanent Address */}
            <Typography variant="subtitle2" sx={{ mt: 3 }}>Permanent Address</Typography>
            <Divider sx={{ my: 1 }} />
            <Grid container spacing={2}>
              {formConfig.addressDetails.map((item) => (
                <Grid item xs={item.xs} key={`permanent-${item.field}`}>
                  <TextField
                    label={item.label}
                    fullWidth
                    value={formData[item.field]}
                    onChange={handleSimpleChange(item.field)}
                  />
                </Grid>
              ))}
            </Grid>
          </Paper>
        </Grid>

        <Grid item xs={12} textAlign="center">
          <Button variant="contained" color="primary" onClick={handleSubmit}>
            Submit
          </Button>
        </Grid>
      </Grid>
    </Box>
  );
};

export default ApplicationFormBPT;
