import React from 'react';
import {
  Box,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper
} from '@mui/material';
import { toWords } from 'number-to-words';

const FormComponentNMT = ({ 
  title,
  subjectsData,
  studentInfo,
  idLabel = 'Roll No'
}) => {
  // Calculate grand total
  const grandTotal = subjectsData.reduce((total, subject) => {
    return total + (subject.totalSecured || 0);
  }, 0);

  // Convert total to words
  const totalInWords = grandTotal > 0 ? toWords(grandTotal).replace(/,/g, ' ').toUpperCase() : '';

  return (
    <Box sx={{ 
      p: 3, 
      maxWidth: 900, 
      mx: 'auto',
      fontFamily: 'Arial',
      border: '1px solid #ccc',
      boxShadow: 3
    }}>
      {/* University Header */}
      <Typography variant="h6" align="center" sx={{ fontWeight: 'bold', mb: 1 }}>
        Nizam's Institute of Medical Sciences
      </Typography>
      <Typography variant="subtitle2" align="center" sx={{ mb: 1, fontStyle: 'italic' }}>
        (A University established under the State Act)
      </Typography>
      <Typography variant="subtitle2" align="center" sx={{ mb: 3 }}>
        PUNJAGUTTA: HYDERABAD - 500 082<br />
        TELANGANA STATE, INDIA
      </Typography>

      {/* Document Title */}
      <Typography variant="h6" align="center" sx={{ 
        fontWeight: 'bold', 
        mb: 3,
        textDecoration: 'underline'
      }}>
        MEMORANDUM OF MARKS
      </Typography>

      {/* Student Information */}
      <Box sx={{ mb: 3 }}>
        <Typography><strong>{idLabel}:</strong> {studentInfo.rollNumber}</Typography>
        <Typography variant="h6" sx={{ fontWeight: 'bold', my: 1 }}>
          {title}
        </Typography>
        <Typography>
          {studentInfo.examTitle} held in the month of {studentInfo.examDate}
        </Typography>
        <Typography><strong>Name:</strong> {studentInfo.name}</Typography>
        <Typography><strong>S/O./D/O:</strong> {studentInfo.parentName}</Typography>
      </Box>

      {/* Marks Table */}
      <TableContainer component={Paper} sx={{ mb: 2 }}>
        <Table size="small" sx={{ border: '1px solid #ddd' }}>
          <TableHead>
            <TableRow sx={{ backgroundColor: '#f5f5f5' }}>
              <TableCell sx={{ fontWeight: 'bold', border: '1px solid #ddd' }}>Paper</TableCell>
              <TableCell sx={{ fontWeight: 'bold', border: '1px solid #ddd' }}>Subject</TableCell>
              <TableCell align="center" colSpan={2} sx={{ fontWeight: 'bold', border: '1px solid #ddd' }}>Theory</TableCell>
              <TableCell align="center" colSpan={2} sx={{ fontWeight: 'bold', border: '1px solid #ddd' }}>Practical/Viva</TableCell>
              <TableCell align="center" colSpan={2} sx={{ fontWeight: 'bold', border: '1px solid #ddd' }}>Total</TableCell>
            </TableRow>
            <TableRow sx={{ backgroundColor: '#f5f5f5' }}>
              <TableCell sx={{ border: '1px solid #ddd' }}></TableCell>
              <TableCell sx={{ border: '1px solid #ddd' }}></TableCell>
              <TableCell align="center" sx={{ border: '1px solid #ddd' }}>Maximum Marks</TableCell>
              <TableCell align="center" sx={{ border: '1px solid #ddd' }}>Marks Secured</TableCell>
              <TableCell align="center" sx={{ border: '1px solid #ddd' }}>Maximum Marks</TableCell>
              <TableCell align="center" sx={{ border: '1px solid #ddd' }}>Marks Secured</TableCell>
              <TableCell align="center" sx={{ border: '1px solid #ddd' }}>Maximum Marks</TableCell>
              <TableCell align="center" sx={{ border: '1px solid #ddd' }}>Marks Secured</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {subjectsData.map((subject, index) => (
              <TableRow key={index}>
                <TableCell sx={{ border: '1px solid #ddd' }}>{subject.paperNo}</TableCell>
                <TableCell sx={{ border: '1px solid #ddd' }}>{subject.subject}</TableCell>
                <TableCell align="center" sx={{ border: '1px solid #ddd' }}>{subject.theoryMax}</TableCell>
                <TableCell align="center" sx={{ border: '1px solid #ddd' }}>{subject.theorySecured || '-'}</TableCell>
                <TableCell align="center" sx={{ border: '1px solid #ddd' }}>{subject.practicalMax || 'NA'}</TableCell>
                <TableCell align="center" sx={{ border: '1px solid #ddd' }}>
                  {subject.practicalMax ? (subject.practicalSecured || '-') : 'NA'}
                </TableCell>
                <TableCell align="center" sx={{ border: '1px solid #ddd' }}>{subject.totalMax}</TableCell>
                <TableCell align="center" sx={{ border: '1px solid #ddd' }}>{subject.totalSecured || '-'}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      {/* Footer Information */}
      <Box sx={{ mt: 2 }}>
        <Typography>
          <strong>Aggregate marks in words:</strong> {totalInWords || '__________'}
        </Typography>
        <Typography sx={{ mt: 1 }}>
          <strong>Result:</strong> {studentInfo.result ? 'PASS' : 'FAIL'}
        </Typography>
        <Typography align="right" sx={{ mt: 3 }}>
          <strong>Dated:</strong> {new Date(studentInfo.submissionDate).toLocaleDateString('en-IN')}<br />
          EXECUTIVE REGISTRAR
        </Typography>
        <Typography variant="caption" sx={{ 
          mt: 2, 
          display: 'block',
          fontStyle: 'italic'
        }}>
          AP: Already Pass; AB: Absent; NA: Not applicable
        </Typography>
      </Box>
    </Box>
  );
};

export default FormComponentNMT;
