import React, {  useState } from 'react';

const FormComponentBonafide = () => {
  const [data, setData] = useState(null);


  if (!data) return <p style={{ textAlign: 'center' }}>Loading certificate...</p>;

  return (
    <div style={{
      position: 'relative',
      padding: '40px',
      fontFamily: 'Georgia, serif',
      maxWidth: '900px',
      margin: 'auto',
      lineHeight: '1.8',
      border: '1px solid #000',
      backgroundColor: '#fff',
    }}>
      {/* Watermark */}
      <div style={{
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%) rotate(-45deg)',
        fontSize: '100px',
        color: '#ccc',
        opacity: 0.2,
        zIndex: 0,
        userSelect: 'none',
        pointerEvents: 'none'
      }}>
        FORM
      </div>



      {/* File No and Date */}
      <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '30px', position: 'relative', zIndex: 1 }}>
        <p><strong>File.No.</strong> {data.fileNo}</p>
        <p><strong>Date:</strong> {data.date}</p>
      </div>

      {/* Title */}
      <h3 style={{
        textAlign: 'center',
        textDecoration: 'underline',
        fontWeight: 'bold',
        marginTop: '40px',
        position: 'relative',
        zIndex: 1
      }}>
        BONAFIDE/STUDY & COURSE COMPLETION CERTIFICATE
      </h3>

      {/* Body Content */}
      <div style={{ position: 'relative', zIndex: 1 }}>
        <p>
          This is to certify that <strong>{data.studentName}</strong> S/o. D/o. <strong>{data.fatherName}</strong> has completed
          <strong> {data.years}</strong> <strong>{data.courseName}</strong> from this Institute during the year
          <strong> {data.academicYear}</strong>. His/Her Roll number is <strong>{data.rollNumber}</strong>.
        </p>

        <p>
          He/She has completed <strong>{data.internshipDuration}</strong> compulsory Internship from
          <strong> {data.internshipFrom}</strong> to <strong>{data.internshipTo}</strong>. During the period of studies at this institute,
          his/her conduct and character were found to be <strong>“Satisfactory”</strong>.
        </p>


      </div>
    </div>
  );
};

export default FormComponentBonafide;
