import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Paper,
  TableContainer
} from '@mui/material';
import { toWords } from 'number-to-words';

const FormComponentAnesthesia = ({ title, subjectsData }) => {
  const [rollNumber] = useState('ANA12345');
  const [name] = useState('Jane Smith');
  const [sod] = useState('Robert Smith'); // S/o or D/o
  const [date] = useState('June 26, 2025');
  const [totalInWords, setTotalInWords] = useState('');

  const totalMaxMarks = subjectsData.reduce((sum, s) => sum + s.maxMarks, 0);
  const totalMinMarks = subjectsData.reduce((sum, s) => sum + s.minMarks, 0);
  const totalSecuredMarks = subjectsData.reduce((sum, s) => sum + s.marksSecured, 0);

  const overallResult = subjectsData.every(subject => subject.marksSecured >= subject.minMarks)
    ? 'Pass'
    : 'Fail';

  useEffect(() => {
    setTotalInWords(toWords(totalSecuredMarks));
  }, [totalSecuredMarks]);

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h6" gutterBottom>{title}</Typography>

      {/* Personal Info Section */}
      <Typography><strong>Roll Number:</strong> {rollNumber}</Typography>
      <Typography><strong>Name:</strong> {name}</Typography>
      <Typography><strong>S/o. / D/o.:</strong> {sod}</Typography>
      <Typography><strong>Date:</strong> {date}</Typography>

      <TableContainer component={Paper} sx={{ mt: 3 }}>
        <Table>
          <TableHead>
            <TableRow sx={{ backgroundColor: '#eeeeee' }}>
              <TableCell>Paper</TableCell>
              <TableCell>Subject</TableCell>
              <TableCell align="right">Max Marks</TableCell>
              <TableCell align="right">Min Marks</TableCell>
              <TableCell align="right">Marks Secured</TableCell>
              <TableCell align="center">Result</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {subjectsData.map((subject, index) => {
              const result = subject.marksSecured >= subject.minMarks ? 'Pass' : 'Fail';
              return (
                <TableRow key={index}>
                  <TableCell>{index + 1}</TableCell>
                  <TableCell>{subject.subject}</TableCell>
                  <TableCell align="right">{subject.maxMarks}</TableCell>
                  <TableCell align="right">{subject.minMarks}</TableCell>
                  <TableCell align="right">{subject.marksSecured}</TableCell>
                  <TableCell align="center">{result}</TableCell>
                </TableRow>
              );
            })}

            <TableRow sx={{ backgroundColor: '#f0f0f0' }}>
              <TableCell colSpan={2}><strong>Total</strong></TableCell>
              <TableCell align="right"><strong>{totalMaxMarks}</strong></TableCell>
              <TableCell align="right"><strong>{totalMinMarks}</strong></TableCell>
              <TableCell align="right"><strong>{totalSecuredMarks}</strong></TableCell>
              <TableCell align="center"><strong>{overallResult}</strong></TableCell>
            </TableRow>
          </TableBody>
        </Table>
      </TableContainer>

      {/* Total in Words */}
      <Box sx={{ mt: 3 }}>
        <Typography><strong>Total Marks in Words:</strong> {totalInWords.toUpperCase()}</Typography>
      </Box>
    </Box>
  );
};

export default FormComponentAnesthesia;
