import React, { useState } from 'react';
import {
  Typography,
  TextField,
  Box,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Button
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { useFormContext } from '../ApplicationBPT/FormContext';

const Application5 = () => {
  const documents = [
    'CASTE CERTIFICATE',
    '10th Class Marks Memo',
    'INTERMEDIATE MARKS MEMO',
    '6TH to 10TH Study and Conduct Certificate',
    'INTER STUDY AND CONDUCT CERTIFICATE',
    'TRANSFER OR MIGRATION CERTIFICATE of Intermediate',
    'TELANGANA STATE EAPCET-2025 RANK CARD'
  ];

  const navigate = useNavigate();
  const { updateFormData } = useFormContext();

  const [paymentData, setPaymentData] = useState({
    paymentType: 'Online Payment',
    transactionId: '136189873101',
    date: '2025-06-09',
    bankName: '',
    branchDetails: '',
    amount: '2500',
    remarks: 'Online Payment'
  });

  const [uploadedDocs, setUploadedDocs] = useState({});

  const handleFileChange = (index, file) => {
    setUploadedDocs(prev => ({
      ...prev,
      [index]: file
    }));
  };

  const handlePaymentChange = (field, value) => {
    setPaymentData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleNext = () => {
    const documentStatus = documents.map((doc, idx) => ({
      name: doc,
      uploaded: !!uploadedDocs[idx],
      file: uploadedDocs[idx] || null
    }));

    updateFormData({
      paymentDetails: paymentData,
      documentUploads: documentStatus
    });

    navigate('/ap6');
  };

  return (
    <Box sx={{ p: 12, border: '1px solid #ccc', borderRadius: 2 }}>
      <Typography variant="h6" gutterBottom>Payment Details</Typography>

      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2, mb: 2 }}>
        <TextField label="Payment Type" value={paymentData.paymentType} onChange={(e) => handlePaymentChange('paymentType', e.target.value)} size="small" />
        <TextField label="Transaction ID" value={paymentData.transactionId} onChange={(e) => handlePaymentChange('transactionId', e.target.value)} size="small" />
        <TextField label="Date" type="date" value={paymentData.date} onChange={(e) => handlePaymentChange('date', e.target.value)} InputLabelProps={{ shrink: true }} size="small" />
      </Box>

      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2, mb: 2 }}>
        <TextField label="Name of Bank" value={paymentData.bankName} onChange={(e) => handlePaymentChange('bankName', e.target.value)} size="small" />
        <TextField label="Branch Details" value={paymentData.branchDetails} onChange={(e) => handlePaymentChange('branchDetails', e.target.value)} size="small" />
        <TextField label="Amount (Rs.)" value={paymentData.amount} onChange={(e) => handlePaymentChange('amount', e.target.value)} size="small" />
      </Box>

      <TextField label="Remarks, If Any" value={paymentData.remarks} onChange={(e) => handlePaymentChange('remarks', e.target.value)} size="small" sx={{ mb: 3 }} />

      <Typography variant="h6" gutterBottom>Document Uploads</Typography>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>S.No.</TableCell>
              <TableCell>Document Type</TableCell>
              <TableCell>Upload File</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {documents.map((doc, idx) => (
              <TableRow key={idx}>
                <TableCell>{idx + 1}</TableCell>
                <TableCell>{doc}</TableCell>
                <TableCell>
                  <input type="file" accept=".pdf,.jpg,.png" onChange={(e) => handleFileChange(idx, e.target.files[0])} />
                  {uploadedDocs[idx] && (
                    <Typography variant="caption" color="primary">
                      {uploadedDocs[idx].name}
                    </Typography>
                  )}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Box sx={{ mt: 3, textAlign: 'right' }}>
        <Button variant="contained" onClick={handleNext}>Submit</Button>
      </Box>
    </Box>
  );
};

export default Application5;
