import React, { useState } from 'react';
import { Typography, TextField, Box } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { Button } from '@mui/material';
import { FormControl, InputLabel, Select, MenuItem } from '@mui/material';
import { useFormContext } from '../ApplicationBPT/FormContext'; // adjust the path as needed

const Application1= () => {
  const [adDetails, setAdDetails] = useState({
    adNo: '',
    adDate: '',
    applicationNo: '',
    courseCode: '',
    courseName: '',
    dateOfEntry: '',
    lastDate: '',

    name: '',
    fatherName: '',
    dob: '',
    age: '',
    placeOfBirth: '',
    socialStatus: '',
    nationality: '',
    maritalStatus: '',
    gender: '',
    differentlyAbled: '',
    idMark1: '',
    idMark2: '',
    universityArea: '',
    inService: '',
    aadhar: '',
    fatherEmail: ''
  });
  const { updateFormData } = useFormContext();
  const [familyDetails, setFamilyDetails] = useState([
    { relation: '', name: '', age: '', occupation: '', income: '' },
    { relation: '', name: '', age: '', occupation: '', income: '' }
  ]);

  const handleAdDetailsChange = (field, value) => {
    setAdDetails(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleFamilyChange = (index, field, value) => {
    const updated = [...familyDetails];
    updated[index][field] = value;
    setFamilyDetails(updated);
  };
  const navigate = useNavigate();

  const handleNext = () => {
    updateFormData({
      ...adDetails,
      familyDetails
    });
    navigate('/ap2');
  };

  return (
    <Box sx={{ p: 6, border: '1px solid #ccc', borderRadius: 2 }}>
      {/* Advertisement Details */}
      <Box sx={{ mb: 6 }}>
        <Typography variant="h6" gutterBottom>Advertisement Details</Typography>


        <TextField fullWidth margin="normal" label="Application No." value={adDetails.applicationNo} onChange={(e) => handleAdDetailsChange('applicationNo', e.target.value)} size="small" />
        <TextField fullWidth margin="normal" label="Course Code" value={adDetails.courseCode} onChange={(e) => handleAdDetailsChange('courseCode', e.target.value)} size="small" />

<FormControl fullWidth margin="normal" size="small">
  <InputLabel>Course Name</InputLabel>
  <Select
    value={adDetails.courseName}
    label="Course Name"
    onChange={(e) => handleAdDetailsChange('courseName', e.target.value)}
  >
    <MenuItem value="BPT">BPT</MenuItem>
    <MenuItem value="B.Sc Nursing">B.Sc Nursing</MenuItem>
    <MenuItem value="MPT">MPT</MenuItem>
    <MenuItem value="Post Basic B.Sc Nursing">Post Basic B.Sc Nursing</MenuItem>
    {/* Add more course options here if needed */}
  </Select>
</FormControl>

        <TextField fullWidth margin="normal" label="Date of Entry" type="datetime-local" value={adDetails.dateOfEntry} onChange={(e) => handleAdDetailsChange('dateOfEntry', e.target.value)} size="small" InputLabelProps={{ shrink: true }} />
        <TextField fullWidth margin="normal" label="Last Date of Receiving Application" type="date" value={adDetails.lastDate} onChange={(e) => handleAdDetailsChange('lastDate', e.target.value)} size="small" InputLabelProps={{ shrink: true }} />

      </Box>

      {/* Applicant's Details */}
      <Box sx={{ mb: 6 }}>
        <Typography variant="h6" gutterBottom>Applicant's Details</Typography>

        <TextField fullWidth margin="normal" label="Name" value={adDetails.name} onChange={(e) => handleAdDetailsChange('name', e.target.value)} size="small" />
        <TextField fullWidth margin="normal" label="Father's Name" value={adDetails.fatherName} onChange={(e) => handleAdDetailsChange('fatherName', e.target.value)} size="small" />
        <TextField fullWidth margin="normal" label="Date of Birth" type="date" value={adDetails.dob} onChange={(e) => handleAdDetailsChange('dob', e.target.value)} size="small" InputLabelProps={{ shrink: true }} />
        <TextField fullWidth margin="normal" label="Age (as on last date)" value={adDetails.age} onChange={(e) => handleAdDetailsChange('age', e.target.value)} size="small" />
        <TextField fullWidth margin="normal" label="Place of Birth" value={adDetails.placeOfBirth} onChange={(e) => handleAdDetailsChange('placeOfBirth', e.target.value)} size="small" />
        <TextField fullWidth margin="normal" label="Social Status" value={adDetails.socialStatus} onChange={(e) => handleAdDetailsChange('socialStatus', e.target.value)} size="small" />
        <TextField fullWidth margin="normal" label="Nationality" value={adDetails.nationality} onChange={(e) => handleAdDetailsChange('nationality', e.target.value)} size="small" />
        <TextField fullWidth margin="normal" label="Marital Status" value={adDetails.maritalStatus} onChange={(e) => handleAdDetailsChange('maritalStatus', e.target.value)} size="small" />
        <TextField fullWidth margin="normal" label="Gender" value={adDetails.gender} onChange={(e) => handleAdDetailsChange('gender', e.target.value)} size="small" />
        <TextField fullWidth margin="normal" label="Differently Abled" value={adDetails.differentlyAbled} onChange={(e) => handleAdDetailsChange('differentlyAbled', e.target.value)} size="small" />
        <TextField fullWidth margin="normal" label="Identification Mark 1" value={adDetails.idMark1} onChange={(e) => handleAdDetailsChange('idMark1', e.target.value)} size="small" />
        <TextField fullWidth margin="normal" label="Identification Mark 2" value={adDetails.idMark2} onChange={(e) => handleAdDetailsChange('idMark2', e.target.value)} size="small" />
        <TextField fullWidth margin="normal" label="University Area" value={adDetails.universityArea} onChange={(e) => handleAdDetailsChange('universityArea', e.target.value)} size="small" />
        <TextField fullWidth margin="normal" label="In-service (Govt.)" value={adDetails.inService} onChange={(e) => handleAdDetailsChange('inService', e.target.value)} size="small" />
        <TextField fullWidth margin="normal" label="Aadhar Number" value={adDetails.aadhar} onChange={(e) => handleAdDetailsChange('aadhar', e.target.value)} size="small" />
        <TextField fullWidth margin="normal" label="Father's Email" value={adDetails.fatherEmail} onChange={(e) => handleAdDetailsChange('fatherEmail', e.target.value)} size="small" />
      </Box>

      {/* Parents / Spouse Section */}
      <Box sx={{ p: 4, border: '1px solid #ccc', borderRadius: 2 }}>
        <Typography variant="h6" gutterBottom>Details of Parents / Spouse</Typography>

        {familyDetails.map((person, index) => (
          <Box key={index} sx={{ mb: 2 }}>
            <Typography variant="subtitle1" sx={{ mb: 1 }}>{person.relation}</Typography>
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2 }}>
              <TextField
                label="Name"
                value={person.name}
                onChange={(e) => handleFamilyChange(index, 'name', e.target.value)}
                size="small"
                sx={{ flex: 1 }}
              />
              <TextField
                label="Age (in years)"
                value={person.age}
                onChange={(e) => handleFamilyChange(index, 'age', e.target.value)}
                size="small"
                sx={{ flex: 1 }}
              />
              <TextField
                label="Occupation"
                value={person.occupation}
                onChange={(e) => handleFamilyChange(index, 'occupation', e.target.value)}
                size="small"
                sx={{ flex: 1 }}
              />
              <TextField
                label="Gross Monthly Income (Rs.)"
                value={person.income}
                onChange={(e) => handleFamilyChange(index, 'income', e.target.value)}
                size="small"
                sx={{ flex: 1 }}
              />

            </Box>

          </Box>

        ))}
        <Box sx={{ mt: 3, textAlign: 'right' }}>
  <Button variant="contained" onClick={handleNext}>Next</Button>
</Box>

      </Box>
    </Box>
  );
};

export default Application1;
