import React, { useState } from 'react';
import { TextField, Box, Typography, Button } from '@mui/material';
import FormComponent4 from '../BscNursing4YDCForms/FormComponent4';
import axios from 'axios';

const NursingSemester1 = () => {
  const [studentId, setStudentId] = useState('');
  const [course, setCourse] = useState('');
  const [department, setDepartment] = useState('');
  const [year, setYear] = useState('');
  const [semester, setSemester] = useState('');

  const [studentDetails, setStudentDetails] = useState(null);
  const [subjectsData, setSubjectsData] = useState([]);
  const [loading, setLoading] = useState(false); // ðŸ‘ˆ Loading state added

  const handleFetch = async () => {
    if (!studentId || !course || !department || !year || !semester) {
      alert("Please fill in all fields before fetching marks.");
      return;
    }

    setLoading(true);
    setStudentDetails(null);
    setSubjectsData([]);

    try {
      const response = await axios.post('http://localhost:4000/api/student/get-marks', {
        studentId,
        course,
        department,
        year,
        semester,
      });

      setStudentDetails(response.data.student);
      setSubjectsData(response.data.subjects);
    } catch (error) {
      console.error("Error fetching student data:", error);
      alert("No data found or server error");
    } finally {
      setLoading(false); // ðŸ‘ˆ Always reset loading
    }
  };

  return (
    <Box sx={{ px: 6, py: 4 }}>
      <Typography variant="h6" gutterBottom>
        Enter Student Details
      </Typography>

      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2, mb: 2 }}>
        <TextField
          label="Student ID"
          value={studentId}
          onChange={(e) => setStudentId(e.target.value)}
        />
        <TextField
          label="Course"
          value={course}
          onChange={(e) => setCourse(e.target.value)}
        />
        <TextField
          label="Department"
          value={department}
          onChange={(e) => setDepartment(e.target.value)}
        />
        <TextField
          label="Year"
          value={year}
          onChange={(e) => setYear(e.target.value)}
        />
        <TextField
          label="Semester"
          value={semester}
          onChange={(e) => setSemester(e.target.value)}
        />

        {/* âœ… Updated Button */}
        <Button variant="contained" onClick={handleFetch} disabled={loading}>
          {loading ? "Fetching..." : "Fetch Marks"}
        </Button>
      </Box>

      {studentDetails && (
        <FormComponent4
          title="First Year B.Sc. Nursing Marks Memo"
          subjectsData={subjectsData}
          idLabel="Student ID:"
          rollNumber={studentDetails.studentId}
          name={studentDetails.name}
          sod={studentDetails.department}
          examDate={`${studentDetails.year} - Semester ${studentDetails.semester}`}
          sgpa="8.9"
        />
      )}
    </Box>
  );
};

export default NursingSemester1;
