import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const inlineStyles = {
  container: { borderRadius: '8px', width: '100%', maxWidth: '800px', margin: '0 auto' },
  field: { marginBottom: '18px' },
  label: { display: 'block', marginBottom: '5px', fontWeight: 'bold' },
  input: { padding: '10px', width: '100%', borderRadius: '4px', border: '1px solid #ccc' },
  textarea: { padding: '8px', width: '100%', borderRadius: '4px', border: '1px solid #ccc' },
  radioGroup: { display: 'flex', gap: '20px', marginTop: '5px' },
  button: {
    padding: '10px 20px',
    border: 'none',
    borderRadius: '4px',
    backgroundColor: '#4b1d77',
    color: '#ffffff',
    cursor: 'pointer',
    fontSize: '15px',
    fontWeight: 500
  }
};

const FormComponent = ({ formTitle, fields }) => {
  const navigate = useNavigate();

  const initialState = fields.reduce((acc, field) => {
    acc[field.name] = '';
    return acc;
  }, {});

  const [formData, setFormData] = useState(initialState);
  const [previewMode, setPreviewMode] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handlePreview = (e) => {
    e.preventDefault();
    setPreviewMode(true);
  };

  const handleEdit = () => {
    setPreviewMode(false);
  };

  const shouldDisplayField = (field) => {
    if (!field.conditional) return true;
    const { field: dependency, value } = field.conditional;
    return formData[dependency] === value;
  };

  return (
    <div style={inlineStyles.container}>
      <h2>{formTitle}</h2>

      {!previewMode ? (
        <form onSubmit={handlePreview}>
          {fields.map((field, index) => {
            if (!shouldDisplayField(field)) return null;

            if (field.type === 'heading') {
              return (
                <h2 key={`heading-${index}`} style={{ margin: '20px 0', color: '#4b1d77' }}>
                  {field.text}
                </h2>
              );
            }

            return (
              <div key={field.name} style={inlineStyles.field}>
                <label style={inlineStyles.label}>{field.label}</label>

                {field.type === 'text' && (
                  <input
                    type="text"
                    name={field.name}
                    value={formData[field.name]}
                    onChange={handleChange}
                    style={inlineStyles.input}
                    required={field.required}
                  />
                )}

                {field.type === 'select' && (
                  <select
                    name={field.name}
                    value={formData[field.name]}
                    onChange={handleChange}
                    style={inlineStyles.input}
                    required={field.required}
                  >
                    <option value="">Select</option>
                    {field.options.map((opt) => (
                      <option key={opt} value={opt}>{opt}</option>
                    ))}
                  </select>
                )}

                {field.type === 'textarea' && (
                  <textarea
                    name={field.name}
                    value={formData[field.name]}
                    onChange={handleChange}
                    rows="4"
                    style={inlineStyles.textarea}
                    required={field.required}
                  />
                )}

                {field.type === 'date' && (
                  <input
                    type="date"
                    name={field.name}
                    value={formData[field.name]}
                    onChange={handleChange}
                    style={inlineStyles.input}
                    required={field.required}
                  />
                )}

                {field.type === 'radio' && (
                  <div style={inlineStyles.radioGroup}>
                    {field.options.map((opt) => (
                      <label key={opt}>
                        <input
                          type="radio"
                          name={field.name}
                          value={opt}
                          checked={formData[field.name] === opt}
                          onChange={handleChange}
                          required={field.required}
                        />{' '}
                        {opt}
                      </label>
                    ))}
                  </div>
                )}
              </div>
            );
          })}

          <div style={{ display: "flex", justifyContent: "center" }}>
            <button type="submit" style={inlineStyles.button}>Preview</button>
          </div>
        </form>
      ) : (
        <>
          <div style={{ padding: '20px', backgroundColor: '#f8f8f8', borderRadius: '8px' }}>
            <h3>Preview</h3>
            {fields.map((field) => (
              shouldDisplayField(field) && field.type !== 'heading' && (
                <div key={field.name} style={{ marginBottom: '12px' }}>
                  <strong>{field.label}:</strong> {formData[field.name]}
                </div>
              )
            ))}
          </div>
          <div style={{ display: "flex", justifyContent: "center", gap: "40px", marginTop: "20px" }}>
            <button onClick={handleEdit} style={{ ...inlineStyles.button, backgroundColor: '#6c757d' }}>Edit</button>
            <button onClick={() => alert("Form submitted!")} style={inlineStyles.button}>Submit</button>
          </div>
        </>
      )}
    </div>
  );
};

export default FormComponent;
