import React, { useState, useRef } from 'react';
import {
  Box, Typography, TextField, Grid, Paper, Table, TableBody,
  TableCell, TableContainer, TableHead, TableRow, Button
} from '@mui/material';
import { useReactToPrint } from 'react-to-print';

const ApplicationReportBPT = () => {
  const [showReport, setShowReport] = useState(false);
  const reportRef = useRef();

  const [formData, setFormData] = useState({
    adNo: '',
    adDate: '',
    name: '',
    fatherName: '',
    subjects: [
      {
        subject: '',
        y1: '',
        y2: '',
        practical: '',
        total: '',
        max: ''
      }
    ]
  });

  const handlePrint = useReactToPrint({
    content: () => reportRef.current,
    pageStyle: `
      @media print {
        body { -webkit-print-color-adjust: exact; }
        .no-print { display: none !important; }
        @page { size: A4; margin: 10mm; }
      }
    `
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    setShowReport(true);
  };

  const handleCloseReport = () => {
    setShowReport(false);
  };

  const handleSubjectChange = (index, field, value) => {
    const updatedSubjects = [...formData.subjects];
    updatedSubjects[index][field] = value;
    setFormData({ ...formData, subjects: updatedSubjects });
  };

  const addSubjectRow = () => {
    setFormData({
      ...formData,
      subjects: [...formData.subjects, {
        subject: '',
        y1: '',
        y2: '',
        practical: '',
        total: '',
        max: ''
      }]
    });
  };

  if (!showReport) {
    return (
      <Box component="form" onSubmit={handleSubmit} sx={{ pt: 4, px: 6, pb: 7 }}>
        <Typography variant="h4" gutterBottom>
          Application Form - Bachelor of Physiotherapy
        </Typography>

        <Grid container spacing={3}>
          <Grid item xs={6}>
            <TextField
              label="Ad. No."
              fullWidth
              value={formData.adNo}
              onChange={(e) => setFormData({ ...formData, adNo: e.target.value })}
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Ad. Date"
              type="date"
              fullWidth
              InputLabelProps={{ shrink: true }}
              value={formData.adDate}
              onChange={(e) => setFormData({ ...formData, adDate: e.target.value })}
            />
          </Grid>

          <Grid item xs={6}>
            <TextField
              label="Name"
              fullWidth
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Father's Name"
              fullWidth
              value={formData.fatherName}
              onChange={(e) => setFormData({ ...formData, fatherName: e.target.value })}
            />
          </Grid>

          {/* Intermediate Subjects Table */}
          <Grid item xs={12}>
            <Typography variant="h6">Intermediate Marks</Typography>
            {formData.subjects.map((subject, index) => (
              <Grid container spacing={2} key={index} sx={{ mb: 2 }}>
                <Grid item xs={2}>
                  <TextField
                    label="Subject"
                    fullWidth
                    value={subject.subject}
                    onChange={(e) => handleSubjectChange(index, 'subject', e.target.value)}
                  />
                </Grid>
                <Grid item xs={2}>
                  <TextField
                    label="1st Year"
                    fullWidth
                    value={subject.y1}
                    onChange={(e) => handleSubjectChange(index, 'y1', e.target.value)}
                  />
                </Grid>
                <Grid item xs={2}>
                  <TextField
                    label="2nd Year"
                    fullWidth
                    value={subject.y2}
                    onChange={(e) => handleSubjectChange(index, 'y2', e.target.value)}
                  />
                </Grid>
                <Grid item xs={2}>
                  <TextField
                    label="Practical"
                    fullWidth
                    value={subject.practical}
                    onChange={(e) => handleSubjectChange(index, 'practical', e.target.value)}
                  />
                </Grid>
                <Grid item xs={2}>
                  <TextField
                    label="Total"
                    fullWidth
                    value={subject.total}
                    onChange={(e) => handleSubjectChange(index, 'total', e.target.value)}
                  />
                </Grid>
                <Grid item xs={2}>
                  <TextField
                    label="Max Marks"
                    fullWidth
                    value={subject.max}
                    onChange={(e) => handleSubjectChange(index, 'max', e.target.value)}
                  />
                </Grid>
              </Grid>
            ))}
            <Button variant="outlined" onClick={addSubjectRow}>
              + Add Subject
            </Button>
          </Grid>

          <Grid item xs={12}>
            <Button type="submit" variant="contained" color="primary">
              Preview Report
            </Button>
          </Grid>
        </Grid>
      </Box>
    );
  }

  return (
    <Box sx={{ p: 4 }} ref={reportRef}>
