import React, { useState } from 'react';
import { TextField, Box, Typography, Button, Snackbar, Alert } from '@mui/material';
import FormComponent4 from '../BscNursing4YDCForms/FormComponent4';
import axios from 'axios';

const FORM_TITLE = "First Year B.Sc. Nursing Marks Memo";
const API_BASE_URL = 'http://localhost:4000/api';

const NursingSemester1 = () => {
  const [formData, setFormData] = useState({
    studentId: '',
    course: '',
    department: '',
    year: '',
    semester: ''
  });

  const [studentDetails, setStudentDetails] = useState(null);
  const [subjectsData, setSubjectsData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleFetch = async () => {
    const { studentId, course, department, year, semester } = formData;

    if (!studentId || !course || !department || !year || !semester) {
      setError("Please fill in all fields before fetching marks.");
      return;
    }

    setLoading(true);
    setStudentDetails(null);
    setSubjectsData([]);
    setError(null);

    try {
      const response = await axios.post(`${API_BASE_URL}/student/get-marks`, {
        studentId,
        course,
        department,
        year,
        semester,
      });

      setStudentDetails(response.data.student);
      setSubjectsData(response.data.subjects);
    } catch (error) {
      console.error("Error fetching student data:", error);
      setError(error.response?.data?.message || "No data found or server error");
    } finally {
      setLoading(false);
    }
  };

  const handleCloseError = () => {
    setError(null);
  };

  return (
    <Box sx={{ px: 6, py: 4 }}>
      <Typography variant="h6" gutterBottom>
        Enter Student Details
      </Typography>

      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2, mb: 2 }}>
        <TextField
          label="Student ID"
          name="studentId"
          value={formData.studentId}
          onChange={handleChange}
        />
        <TextField
          label="Course"
          name="course"
          value={formData.course}
          onChange={handleChange}
        />
        <TextField
          label="Department"
          name="department"
          value={formData.department}
          onChange={handleChange}
        />
        <TextField
          label="Year"
          name="year"
          value={formData.year}
          onChange={handleChange}
          type="number"
        />
        <TextField
          label="Semester"
          name="semester"
          value={formData.semester}
          onChange={handleChange}
        />

        <Button 
          variant="contained" 
          onClick={handleFetch} 
          disabled={loading}
        >
          {loading ? "Fetching..." : "Sh"}
        </Button>
      </Box>

      {studentDetails && (
        <FormComponent4
          title={FORM_TITLE}
          subjectsData={subjectsData}
          idLabel="Student ID:"
          rollNumber={studentDetails.studentId}
          name={studentDetails.name}
          sod={studentDetails.department}
          examDate={`${studentDetails.year} - Semester ${studentDetails.semester}`}
        />
      )}

      <Snackbar
        open={!!error}
        autoHideDuration={6000}
        onClose={handleCloseError}
      >
        <Alert onClose={handleCloseError} severity="error" sx={{ width: '100%' }}>
          {error}
        </Alert>
      </Snackbar>
    </Box>
  );
};tetch

export default NursingSemester1;
