import React, { useReducer, useMemo } from 'react';
import {
  Box, Typography, TextField, Grid, Paper, Button, Divider, TableContainer, Table, TableHead, TableRow, TableCell, TableBody,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { debounce } from 'lodash';

const initialState = {
  adNo: '', adDate: '', applicationNo: '', courseName: '', courseCode: '', dateOfEntry: '', lastDate: '', department: '',
  name: '', fatherName: '', dob: '', age: '', birthPlace: '', status: '', nationality: '', maritalStatus: '',
  gender: '', differentlyAbled: '', idMark1: '', idMark2: '', universityArea: '', inService: '', aadhar: '',
  fatherEmail: '', fatherAge: '', fatherOccupation: '', motherName: '', motherAge: '', motherOccupation: '',
  monthlyIncome: '', address: '', country: '', district: '', state: '', pincode: '', mobileno: '', email: '',
  botany: { theory1stYear: '', theory1stMax: '', theory2ndYear: '', theory2ndMax: '', practical: '', practicalMax: '', total: '', totalMax: '', maxmarks: '' },
  zoology: { theory1stYear: '', theory1stMax: '', theory2ndYear: '', theory2ndMax: '', practical: '', practicalMax: '', total: '', totalMax: '', maxmarks: '' },
  physics: { theory1stYear: '', theory1stMax: '', theory2ndYear: '', theory2ndMax: '', practical: '', practicalMax: '', total: '', totalMax: '', maxmarks: '' },
  chemistry: { theory1stYear: '', theory1stMax: '', theory2ndYear: '', theory2ndMax: '', practical: '', practicalMax: '', total: '', totalMax: '' },
  english: { theory1stYear: '', theory1stMax: '', theory2ndYear: '', theory2ndMax: '', practical: '', practicalMax: '', total: '', totalMax: '' },
  secondLanguage: { theory1stYear: '', theory1stMax: '', theory2ndYear: '', theory2ndMax: '', practical: '', practicalMax: '', total: '', totalMax: '' },
  overallMarks: '', overallMax: '', overallPercentage: '', groupMarks: '', groupMax: '', groupPercentage: '',
  inter2ndYear: '', inter2ndCollege: '', inter2ndPlace: '', inter2ndState: '', inter2ndDistrict: '',
  inter1stYear: '', inter1stCollege: '', inter1stPlace: '', inter1stState: '', inter1stDistrict: '',
  tenthYear: '', tenthSchool: '', tenthPlace: '', tenthState: '', tenthDistrict: '',
  ninthYear: '', ninthSchool: '', ninthPlace: '', ninthState: '', ninthDistrict: '',
  eighthYear: '', eighthSchool: '', eighthPlace: '', eighthState: '', eighthDistrict: '',
  seventhYear: '', seventhSchool: '', seventhPlace: '', seventhState: '', seventhDistrict: '',
  sixthYear: '', sixthSchool: '', sixthPlace: '', sixthState: '', sixthDistrict: '',
  eapcetRegNo: '', eapcetHallTicket: '', eapcetRank: '',
};

const reducer = (state, action) => {
  switch (action.type) {
    case 'UPDATE_SIMPLE_FIELD':
      return { ...state, [action.field]: action.value };
    case 'UPDATE_SUBJECT_FIELD':
      return {
        ...state,
        [action.subject]: {
          ...state[action.subject],
          [action.field]: action.value,
        },
      };
    default:
      return state;
  }
};

const SubjectRow = React.memo(({ subject, subjectName, formData, handleSubjectChange }) => (
  <TableRow>
    <TableCell sx={{ fontWeight: 'bold' }}>{subjectName}</TableCell>
    <TableCell align="center">
      <TextField
        size="small"
        value={formData[subject].theory1stYear}
        onChange={handleSubjectChange(subject, 'theory1stYear')}
        InputProps={{
          endAdornment: formData[subject].theory1stMax && ` / ${formData[subject].theory1stMax}`,
          sx: { '& input': { textAlign: 'right' } },
        }}
        sx={{ width: 100 }}
      />
    </TableCell>
    <TableCell align="center">
      <TextField
        size="small"
        value={formData[subject].theory2ndYear}
        onChange={handleSubjectChange(subject, 'theory2ndYear')}
        InputProps={{
          endAdornment: formData[subject].theory2ndMax && ` / ${formData[subject].theory2ndMax}`,
          sx: { '& input': { textAlign: 'right' } },
        }}
        sx={{ width: 100 }}
      />
    </TableCell>
    <TableCell align="center">
      <TextField
        size="small"
        value={formData[subject].practical}
        onChange={handleSubjectChange(subject, 'practical')}
        InputProps={{
          endAdornment: formData[subject].practicalMax && ` / ${formData[subject].practicalMax}`,
          sx: { '& input': { textAlign: 'right' } },
        }}
        sx={{ width: 100 }}
      />
    </TableCell>
    <TableCell align="center">
      <TextField
        size="small"
        value={formData[subject].total}
        onChange={handleSubjectChange(subject, 'total')}
        InputProps={{
          endAdornment: formData[subject].totalMax && ` / ${formData[subject].totalMax}`,
          sx: { '& input': { textAlign: 'right' } },
        }}
        sx={{ width: 100 }}
      />
    </TableCell>
    <TableCell align="center">
      <TextField
        size="small"
        value={formData[subject].maxmarks}
        onChange={handleSubjectChange(subject, 'maxmarks')}
        InputProps={{
          endAdornment: formData[subject].maxmarks && ` / ${formData[subject].maxmarks}`,
          sx: { '& input': { textAlign: 'right' } },
        }}
        sx={{ width: 100 }}
      />
    </TableCell>
  </TableRow>
));

const ApplicationFormBPT = () => {
  const navigate = useNavigate();
  const [formData, dispatch] = useReducer(reducer, initialState);

  const debouncedHandleSimpleChange = debounce((field, value) => {
    dispatch({ type: 'UPDATE_SIMPLE_FIELD', field, value });
  }, 300);

  const debouncedHandleSubjectChange = debounce((subject, field, value) => {
    dispatch({ type: 'UPDATE_SUBJECT_FIELD', subject, field, value });
  }, 300);

  const handleSimpleChange = (field) => (e) => {
    debouncedHandleSimpleChange(field, e.target.value);
  };

  const handleSubjectChange = (subject, field) => (e) => {
    debouncedHandleSubjectChange(subject, field, e.target.value);
  };

  const handleSubmit = () => {
    navigate('/report', { state: formData });
  };

  const subjects = [
