import React from 'react';
import { Box, Typography, Divider } from '@mui/material';

const formatKey = (key) =>
  key.replace(/_/g, ' ')
     .replace(/([a-z])([A-Z])/g, '$1 $2')
     .replace(/\b\w/g, (l) => l.toUpperCase());

const formatSectionTitle = (section) =>
  section.replace(/([A-Z])/g, ' $1')
         .replace(/\b\w/g, (l) => l.toUpperCase())
         .trim();

const ApplicationReportBPT = ({ formData }) => {
  // ✅ Prevent rendering when formData is null or undefined
  if (!formData || typeof formData !== 'object') {
    return (
      <Box sx={{ p: 4 }}>
        <Typography variant="h6" color="error">No application data available to generate report.</Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ p: 4 }}>
      <Typography variant="h4" gutterBottom>Application Report</Typography>

      {Object.entries(formData).map(([section, data]) => (
        <Box key={section} sx={{ my: 3 }}>
          <Typography variant="h6" color="primary">{formatSectionTitle(section)}</Typography>
          <Divider sx={{ mb: 1 }} />

          {Array.isArray(data) ? (
            <Box sx={{ pl: 2 }}>
              {data.map((item, idx) => (
                <Box key={idx} sx={{ mb: 2 }}>
                  {typeof item === 'object' && item !== null ? (
                    Object.entries(item).map(([key, value]) => (
                      <Typography key={key}>
                        <strong>{formatKey(key)}:</strong> {value?.name || value?.toString() || 'N/A'}
                      </Typography>
                    ))
                  ) : (
                    <Typography>{item?.toString()}</Typography>
                  )}
                  <Divider sx={{ my: 1 }} />
                </Box>
              ))}
            </Box>
          ) : typeof data === 'object' && data !== null ? (
            Object.entries(data).map(([key, value]) => (
              <Typography key={key} sx={{ pl: 2 }}>
                <strong>{formatKey(key)}:</strong> {value?.toString() || 'N/A'}
              </Typography>
            ))
          ) : (
            <Typography sx={{ pl: 2 }}>{JSON.stringify(data)}</Typography>
          )}
        </Box>
      ))}
    </Box>
  );
};

export default ApplicationReportBPT;
