import React, { useState } from 'react';
import { Box, TextField, Typography, Button, Radio, RadioGroup, FormControlLabel } from '@mui/material';

const FormComponent = ({ formTitle, fields }) => {
  const initialState = fields.reduce((acc, field) => {
    acc[field.name] = '';
    return acc;
  }, {});

  const [formData, setFormData] = useState(initialState);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const shouldDisplayField = (field) => {
    if (!field.conditional) return true;
    const { field: dependency, value } = field.conditional;
    return formData[dependency] === value;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
    alert('Form submitted!');
  };

  return (
    <Box sx={{ p: 10, border: '1px solid #ccc', borderRadius: 2, maxWidth: '800px', margin: '0 auto' }}>
      <Typography variant="h6" gutterBottom>{formTitle}</Typography>

      <form onSubmit={handleSubmit}>
        <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2, mb: 3 }}>
          {fields.map((field) => {
            if (!shouldDisplayField(field)) return null;

            if (field.type === 'heading') {
              return (
                <Box key={field.text} sx={{ width: '100%', mt: 2, mb: 1 }}>
                  <Typography variant="h6" sx={{ fontWeight: 'bold', color: '#' }}>
                    {field.text}
                  </Typography>
                </Box>
              );
            }

            switch (field.type) {
              case 'text':
              case 'date':
                return (
                  <TextField
                    key={field.name}
                    type={field.type}
                    label={field.label}
                    name={field.name}
                    value={formData[field.name]}
                    onChange={handleChange}
                    size="small"
                    fullWidth
                    required={field.required}
                    InputLabelProps={field.type === 'date' ? { shrink: true } : {}}
                  />
                );

              case 'textarea':
                return (
                  <TextField
                    key={field.name}
                    label={field.label}
                    name={field.name}
                    multiline
                    rows={4}
                    value={formData[field.name]}
                    onChange={handleChange}
                    size="small"
                    fullWidth
                    required={field.required}
                  />
                );

              case 'radio':
                return (
                  <Box key={field.name} sx={{ mb: 2, width: '100%' }}>
                    <Typography>{field.label}</Typography>
                    <RadioGroup
                      row
                      name={field.name}
                      value={formData[field.name]}
                      onChange={handleChange}
                    >
                      {field.options.map(opt => (
                        <FormControlLabel key={opt} value={opt} control={<Radio required={field.required} />} label={opt} />
                      ))}
                    </RadioGroup>
                  </Box>
                );

              default:
                return null;
            }
          })}
        </Box>

        <Box sx={{ textAlign: 'right' }}>
          <Button variant="contained" type="submit">Submit</Button>
        </Box>
      </form>
    </Box>
  );
};

export default FormComponent;
