import React, { useEffect, useState } from 'react';
import { Box, Typography, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, TextField, Button } from '@mui/material';
import { toWords } from 'number-to-words';

const FormComponentMPT2 = ({ rollNumber, name, sod, examDate, subjectsData, specialty }) => {
  const [inputRollNumber, setInputRollNumber] = useState('');
  const [department, setDepartment] = useState('');
  const [year, setYear] = useState('');
  const [academicyear, setAcademicYear] = useState('');
  const [grandTotal, setGrandTotal] = useState(0);
  const [totalInWords, setTotalInWords] = useState('');

  const handleFetch = () => {
    console.log('Fetching data for roll number:', inputRollNumber);
  };

  useEffect(() => {
    const total = subjectsData.reduce((acc, subj) => acc + Number(subj.totalSecured || 0), 0);
    setGrandTotal(total);
    setTotalInWords(toWords(total));
  }, [subjectsData]);

  return (
    <Box sx={{ pt: 9, px: 6, pb: 7 }}>
      <Box sx={{ mb: 3, display: 'flex', flexDirection: 'column', gap: 2 }}>
        <TextField label="Student ID" value={inputRollNumber} onChange={(e) => setInputRollNumber(e.target.value)} />
        <TextField label="Department" value={department} onChange={(e) => setDepartment(e.target.value)} />
        <TextField label="Year of Study" value={year} onChange={(e) => setYear(e.target.value)} />
        <TextField label="Academic Year" value={academicyear} onChange={(e) => setAcademicYear(e.target.value)} />
        <Button variant="outlined" onClick={handleFetch}>Fetch</Button>
      </Box>

      {/* Remaining UI code stays same */}
      {/* Table & Result Display */}
    </Box>
  );
};

export default FormComponentMPT2;
